mapper.update:
UPDATE medi_member SET 
PWD=#{pwd}, EMAIL=#{email},POST=#{post},ADDRESS=#{address}
WHERE ID=#{id}

UPDATE medi_member
SET DEL=1
WHERE ID=#{id}

mapper.select:
SELECT SEQ, ID, PWD, NAME, EMAIL, PHONE, POST, ADDRESS, QUESTION, ANSWER, AUTH, DEL, MYIMG, BLACKLIST, REGDATE
FROM medi_member 
WHERE ID=#{id} AND PWD=#{pwd}
