mapper.update:
UPDATE medi_member
SET DEL=1
WHERE ID=#{id} AND PWD=#{pwd}

mapper.select:
SELECT SEQ, ID, PWD, NAME, EMAIL, PHONE, POST, ADDRESS, QUESTION, ANSWER, AUTH, DEL, MYIMG, BLACKLIST, REGDATE
FROM medi_member 
WHERE ID=#{id} AND PWD=#{pwd}

SELECT IFNULL(COUNT(*), 0) FROM `MEDI`.`medi_question_bbs` WHERE 1=1 AND DEL=0 AND ID=#{id}
AND TITLE LIKE CONCAT('%',#{s_keyword},'%')
AND ID LIKE CONCAT('%',#{s_keyword},'%')

SELECT SEQ, ID, REF, STEP, DEPTH, QUESTION, TITLE, CONTENT, WDATE, MEMCHOICE, PARENT, DEL, READCOUNT, ROCK FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS ROWNUM, SEQ, ID, REF, STEP, DEPTH, QUESTION, TITLE, CONTENT, WDATE, MEMCHOICE, PARENT, DEL, READCOUNT, ROCK FROM `MEDI`.`medi_question_bbs`, (SELECT @ROWNUM := 0) R WHERE 1=1 AND DEL=0 AND ID=#{id}
AND TITLE LIKE CONCAT('%',#{s_keyword},'%')
AND ID LIKE CONCAT('%',#{s_keyword},'%')
ORDER BY SEQ ASC ) A WHERE A.ROWNUM BETWEEN ${start} AND ${end}

SELECT SEQ, MEM_SEQ,WID, TITLE, CONTENT, CATEGORY, READCOUNT, COMMENTCOUNT, SELECTYN,DEL, WDATE FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS ROWNUM, SEQ, MEM_SEQ,WID, TITLE, CONTENT, CATEGORY, READCOUNT, COMMENTCOUNT, SELECTYN,DEL, WDATE FROM `MEDI`.`medi_consulting_question`, (SELECT @ROWNUM := 0) R WHERE 1=1 AND DEL=0 AND MEM_SEQ=#{seq}
AND category LIKE CONCAT('%',#{s_keyword},'%')
AND title LIKE CONCAT('%',#{s_keyword},'%')
AND content LIKE CONCAT('%',#{s_keyword},'%')
ORDER BY SEQ DESC ) A WHERE A.ROWNUM BETWEEN ${start} AND ${end}

 
		SELECT SEQ, MEM_SEQ,WID, TITLE, CONTENT, CATEGORY, READCOUNT, COMMENTCOUNT, SELECTYN,DEL, WDATE
		FROM `MEDI`.`medi_consulting_question` 
		WHERE SEQ=#{seq}
